import React, { Component } from "react";
import "./style.css";
import API from "../utils/API";
// import { resolve } from "dns";

class Search extends Component {
    state = {
        books: []
    }

    componentDidMount() {

    }

    saveBook() {
        API.saveBook()
        .then(res => {
            alert("Saved!")
        })
    }

    handleInputChange = (e) => {
        const { name, value } = e.target;
        this.setState({ [name]: value })
    }

    searchMyBook(search) {
        console.log("search")
        API.searchBook()
        .then(res => {
            console.log("searched")
            this.setState({
                books: res
            })
        })
    }

    render() {
        return (
            <div>
                <div className="saved">
                    <h3 className="left">Search</h3>
                    <h5 className="left">Book</h5>
                    <form>
                        <input
                            name="Search"
                            placeholder="Title"
                            value={this.state.title}
                            onChange={this.handleInputchange}>
                        </input>
                        <button
                            onClick={this.search}>
                        Search
                        </button>
                    </form>
                </div>



                {/* SHOW RESULTS */}
                <div className="saved">
                    <h3 className="left">Results</h3>
                    {this.state.books.map(book => (
                        <div className="book">
                            <div className="top">
                                <h3>{book.volumeInfo.title}</h3>
                                <div className="button">
                                    <a href={book.accessInfo.webReaderLink}>View</a>
                                    <button onClick={this.saveBook}>Save</button>
                                </div>
                            </div>
                            <p>Written By: {book.volumeInfo.authors[0]}</p>
                            <div className="body">
                                <img src={book.volumeInfo.imageLinks.thumbnail} alt="Book"></img>
                                <p>{book.volumeInfo.description}</p>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        )
    };
}

export default Search;
